mapper.select:
SELECT sname, start_time, end_time, bnum, fcategory_mikey,
sstatus_mikey, stel, saddr
FROM shop;

SELECT sstatus_mikey, bnum
FROM shop
WHERE bnum = #{bnum}

mapper.update:
UPDATE shop
SET start_time = #{start_time},
end_time =
#{end_time}
WHERE bnum = #{bnum}

mapper.select:
SELECT bstaus_makey,
bstaus_mikey, mnum from shop where mnum
= #{mnum}
