mapper.select:
select sname, start_time, end_time, bnum
from shop
where fcategory_mikey = #{fcategory_mikey};

select sname, saddr, stel, start_time, end_time, sstatus_mikey,bnum
from shop
where sname = #{sname};

select bnum, fnum, fname, fprice, finfo, foption
from menu
where bnum = #{bnum};

select fname, fnum, fname, fprice, finfo, foption
from menu
where fnum = #{fnum};
